import React from "react";
import { Accordion, AccordionItem, AccordionItemButton, AccordionItemHeading, AccordionItemPanel } from "react-accessible-accordion";
import classes from "./Forecast.module.css";

const WEEKS_DAYS = ['Monday', 'Tuesday', "Wednesday", 'Thursday', 'Friday', 'Saterday', 'Sunday'];

const Forecast = ({ props }) => {
    const dayInAWeek = new Date().getDay();
    const forecastDays = WEEKS_DAYS.slice(dayInAWeek, WEEKS_DAYS.length).concat(WEEKS_DAYS.slice(0, dayInAWeek));  

    console.log(forecastDays);


  return (
    <React.Fragment>
      <label className={classes.title}>Daily</label>
      <Accordion allowZeroExpanded>
        {props.list.slice(0, 7).map((item, i) => (
          <AccordionItem key={i}>
            <AccordionItemHeading>
                <AccordionItemButton>
                    <div className={classes.dailyitem}>
                        <img alt="weather" className={classes.icon} src={`icons/${item.weather[0].icon}.png`}></img>
                        <label className={classes.day}></label>
                    </div>
                </AccordionItemButton>
            </AccordionItemHeading>
            <AccordionItemPanel></AccordionItemPanel>
          </AccordionItem>
        ))}
      </Accordion>
    </React.Fragment>
  );
};
export default Forecast;
