import Search from "./components/search/Search";
import CurrentWeather from "./components/current-weather/Current-weather";
import classes from "./App.module.css";
import { WEATHER_API_URL, WEATHER_API_KEY } from "./Api";
import React, { useState, useEffect, useContext } from "react";
import Forecast from "./components/forecast/Forecast";
import LoadingSpinner from "./components/UI/LoadingSpinner";

function App() {
  const [currentWeather, setCurrentWeather] = useState(null);
  const [forecast, setForecast] = useState(null);
  const [status, setStatus] = useState(false);
  const [latitude, setLatitude] = useState(null);
  const [longitude, setLongitude] = useState(null);
  const [stats, setStats] = useState(null);

  const getLocation = () => {
    if (!navigator.geolocation) {
      setStats("Geolocation is not supported by your browser");
    } else {
      setStats("Locating...");
      navigator.geolocation.getCurrentPosition(
        (position) => {
          setStats(null);
          setLatitude(position.coords.latitude);
          setLongitude(position.coords.longitude);
        },
        () => {
          setStatus("unnable to retrieve your location");
        }
      );
    }
  };
  useEffect(() =>{
  getLocation();
  },[])

  const yourWeather = () => {
    const currentWeatherFetch = fetch(
      `${WEATHER_API_URL}/weather?lat=${latitude}&lon=${longitude}&appid=${WEATHER_API_KEY}&units=metric`
    );
    const forcastFetch = fetch(
      `${WEATHER_API_URL}/forecast?lat=${latitude}&lon=${longitude}&appid=${WEATHER_API_KEY}&units=metric`
    );
    Promise.all([currentWeatherFetch, forcastFetch])
    .then(async (response) => {
      const weatherResponse = await response[0].json();
      const forcastResponse = await response[1].json();

      setCurrentWeather({ city: weatherResponse });
      setForecast({ city: forcastResponse });
    })
    .catch((err) => console.log(err));

  }


  const searchChangeHandler = (searchData) => {
    console.log("HIII")
    const [lat, lon] = searchData.value.split(" ");
    setStatus(true);
    const currentWeatherFetch = fetch(
      `${WEATHER_API_URL}/weather?lat=${lat}&lon=${lon}&appid=${WEATHER_API_KEY}&units=metric`
    );
    const forcastFetch = fetch(
      `${WEATHER_API_URL}/forecast?lat=${lat}&lon=${lon}&appid=${WEATHER_API_KEY}&units=metric`
    );

    Promise.all([currentWeatherFetch, forcastFetch])
      .then(async (response) => {
        const weatherResponse = await response[0].json();
        const forcastResponse = await response[1].json();

        setCurrentWeather({ city: searchData.label, ...weatherResponse });
        setForecast({ city: searchData.label, ...forcastResponse });
      })
      .catch((err) => console.log(err));
  };
  let background = classes.defaultBackground;
  if (currentWeather !== null) {
    let weatherIcon = currentWeather.weather[0].icon;
    if (weatherIcon === "01d" || weatherIcon === "02d") {
      background = classes.sunnyday;
    } else if (weatherIcon === "03d" || weatherIcon === "04d") {
      background = classes.cloudyday;
    } else if (weatherIcon === "09d" || weatherIcon === "10d") {
      background = classes.rainyday;
    } else if (weatherIcon === "11d") {
      background = classes.stormyday;
    } else if (weatherIcon === "13d") {
      background = classes.snowyday;
    } else if (weatherIcon === "50d") {
      background = classes.foggyday;
    } else if (weatherIcon === "01n") {
      background = classes.cloudlessnight;
    } else if (
      weatherIcon === "02n" ||
      weatherIcon === "03n" ||
      weatherIcon === "04n"
    ) {
      background = classes.cloudynight;
    } else if (weatherIcon === "09n" || weatherIcon === "10n") {
      background = classes.rainynight;
    } else if (weatherIcon === "11n") {
      background = classes.stormynight;
    } else if (weatherIcon === "13n") {
      background = classes.snowynight;
    } else if (weatherIcon === "50n") {
      background = classes.foggynight;
    }
  }
  console.log(forecast);
  useEffect(() => {
    if (currentWeather != null && forecast != null) {
      setStatus(false);
    }
  }, [currentWeather, forecast]);

  return (
    <React.Fragment>
      <div>
        <img className={background}></img>
      </div>
      <div className={classes.container}>
        <div className="overlay" />
        <Search onSearchChange={yourWeather} />
        {status && (
          <div className="centered">
            <LoadingSpinner />
          </div>
        )}
        {!status && currentWeather && <CurrentWeather props={currentWeather} />}
        {!status && forecast && <Forecast props={forecast} />}
      </div>
    </React.Fragment>
  );
}

export default App;
