import Search from "./components/search/Search";
import CurrentWeather from "./components/current-weather/Current-weather";
import classes from "./App.module.css";
import { WEATHER_API_URL, WEATHER_API_KEY } from "./Api";
import React, { useState } from "react";
import Forecast from "./components/forecast/Forecast";
import background from "./img/overlay.jpg";

function App() {
  // useEffect(() => {
  //   if(someStateYouExpectToChangeToTriggerTheBackgroundChange === 'what you expect it to be to trigger the bg change') {
  //     document.body.style.backgroundImage = 'url(path/img.png)' // double check my quotes
  //   }
  // }, [someStateYouExpectToChangeToTriggerTheBackgroundChange])
  const [currentWeather, setCurrentWeather] = useState(null);
  const [forecast, setForecast] = useState(null);

  const searchChangeHandler = (searchData) => {
    const [lat, lon] = searchData.value.split(" ");

    const currentWeatherFetch = fetch(
      `${WEATHER_API_URL}/weather?lat=${lat}&lon=${lon}&appid=${WEATHER_API_KEY}&units=metric`
    );
    const forcastFetch = fetch(
      `${WEATHER_API_URL}/forecast?lat=${lat}&lon=${lon}&appid=${WEATHER_API_KEY}&units=metric`
    );

    Promise.all([currentWeatherFetch, forcastFetch])
      .then(async (response) => {
        const weatherResponse = await response[0].json();
        const forcastResponse = await response[1].json();

        setCurrentWeather({ city: searchData.label, ...weatherResponse });
        setForecast({ city: searchData.label, ...forcastResponse });
      })
      .catch((err) => console.log(err));
  };

  return (
    <div>

    <div className={classes.container}>

      <div className="overlay" />
      <div className={classes.body}>
      <Search onSearchChange={searchChangeHandler} />
      {currentWeather && <CurrentWeather props={currentWeather} />}
      {forecast && <Forecast props={forecast} />}
      </div>
    </div>
    </div>
  );
}

export default App;
